// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
// import './App.css'; // Możesz zakomentować lub usunąć, jeśli plik nie istnieje lub go nie chcesz

// Łączymy się z backendem (domyślnie localhost:3001)
const socket = io('http://localhost:3001');

function App() {
  const [isConnected, setIsConnected] = useState(socket.connected);
  const [serverResponse, setServerResponse] = useState('');

  useEffect(() => {
    socket.on('connect', () => {
      setIsConnected(true);
      console.log('Połączono z serwerem WebSocket');
    });

    socket.on('disconnect', () => {
      setIsConnected(false);
      console.log('Rozłączono z serwerem');
    });

    socket.on('response_event', (data) => {
      console.log('Przyszła odpowiedź:', data);
      setServerResponse(data.reply);
    });

    return () => {
      socket.off('connect');
      socket.off('disconnect');
      socket.off('response_event');
    };
  }, []);

  const sendTestMessage = () => {
    socket.emit('test_event', { message: 'Cześć serwer, tu Twój Frontend na Macu!' });
  }

  return (
    <div style={{ padding: '20px', fontFamily: 'sans-serif', backgroundColor: '#222', color: 'white', minHeight: '100vh' }}>
      <h1>Panel Sterowania Passat B7</h1>

      <div style={{ marginBottom: '20px' }}>
        Status połączenia: <strong style={{ color: isConnected ? '#4caf50' : '#f44336' }}>
          {isConnected ? 'POŁĄCZONO' : 'ROZŁĄCZONO'}
        </strong>
      </div>

      <button 
        onClick={sendTestMessage} 
        style={{ 
          fontSize: '16px', 
          padding: '12px 24px', 
          cursor: 'pointer', 
          backgroundColor: '#007bff', 
          color: 'white', 
          border: 'none', 
          borderRadius: '4px' 
        }}
      >
        Wyślij test do Backend
      </button>

      {serverResponse && (
        <div style={{ marginTop: '20px', padding: '10px', border: '1px solid #4caf50', borderRadius: '4px' }}>
          <h3>Odpowiedź z serwera:</h3>
          <p>"{serverResponse}"</p>
        </div>
      )}
    </div>
  );
}

export default App;
