import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

// Łączymy się z backendem
const socket = io('http://localhost:3001');

function App() {
  const [isConnected, setIsConnected] = useState(socket.connected);
  // Stan dla danych z czujników parkowania (domyślnie same zera)
  const [pdcData, setPdcData] = useState({
    front_left: 0,
    front_right: 0,
    rear_left: 0,
    rear_right: 0
  });
  // Stan dla ostatniej akcji z kierownicy
  const [lastAction, setLastAction] = useState(null);

  useEffect(() => {
    socket.on('connect', () => setIsConnected(true));
    socket.on('disconnect', () => setIsConnected(false));

    // 1. Odbieramy dane czujników i aktualizujemy stan
    socket.on('pdc_data', (data) => {
      setPdcData(data);
    });

    // 2. Odbieramy akcje z kierownicy
    socket.on('steering_wheel', (action) => {
      setLastAction(action);
      // Czyścimy komunikat po 2 sekundach
      setTimeout(() => setLastAction(null), 2000);
    });

    return () => {
      socket.off('connect');
      socket.off('disconnect');
      socket.off('pdc_data');
      socket.off('steering_wheel');
    };
  }, []);

  // Funkcja pomocnicza: zamienia odległość (liczbę) na kolor
  // Zakładamy, że symulacja wysyła 1 (bardzo blisko) do 5 (daleko)
  const getColor = (value) => {
    if (value <= 1) return '#ff0000'; // Czerwony (STOP!)
    if (value <= 3) return '#ffcc00'; // Żółty (Uważaj)
    return '#00ff00';                 // Zielony (Bezpiecznie)
  };

  return (
    <div style={styles.container}>
      <h1>Passat B7 - Dashboard</h1>

      <div style={styles.statusBar}>
        Połączenie: <strong style={{ color: isConnected ? '#4caf50' : 'red' }}>
          {isConnected ? 'ONLINE' : 'OFFLINE'}
        </strong>
      </div>

      {/* Wizualizacja samochodu i czujników */}
      <div style={styles.carContainer}>

        {/* Przód samochodu */}
        <div style={styles.sensorRow}>
          <SensorBox label="FL" value={pdcData.front_left} color={getColor(pdcData.front_left)} />
          <div style={styles.carBodyFront}>PRZÓD</div>
          <SensorBox label="FR" value={pdcData.front_right} color={getColor(pdcData.front_right)} />
        </div>

        {/* Środek samochodu */}
        <div style={styles.carBodyCenter}>
            {lastAction && <div style={styles.actionToast}>Kierownica: {lastAction}</div>}
        </div>

        {/* Tył samochodu */}
        <div style={styles.sensorRow}>
          <SensorBox label="RL" value={pdcData.rear_left} color={getColor(pdcData.rear_left)} />
          <div style={styles.carBodyRear}>TYŁ</div>
          <SensorBox label="RR" value={pdcData.rear_right} color={getColor(pdcData.rear_right)} />
        </div>

      </div>

      {/* Podgląd surowych danych (dla debugowania) */}
      <div style={styles.debugBox}>
        <pre>{JSON.stringify(pdcData, null, 2)}</pre>
      </div>
    </div>
  );
}

// Mały komponent wyświetlający pojedynczy czujnik
const SensorBox = ({ label, value, color }) => (
  <div style={{ ...styles.sensor, backgroundColor: color }}>
    <strong>{label}</strong>
    <br />
    {value}
  </div>
);

// Style CSS-in-JS (dla prostoty w jednym pliku)
const styles = {
  container: {
    backgroundColor: '#282c34',
    minHeight: '100vh',
    color: 'white',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '20px',
    fontFamily: 'Arial, sans-serif'
  },
  statusBar: { marginBottom: '20px' },
  carContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    backgroundColor: '#444',
    padding: '20px',
    borderRadius: '15px',
    width: '300px'
  },
  sensorRow: {
    display: 'flex',
    justifyContent: 'space-between',
    width: '100%',
    alignItems: 'center',
    marginBottom: '10px',
    marginTop: '10px'
  },
  sensor: {
    width: '60px',
    height: '60px',
    borderRadius: '8px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    color: '#000',
    fontWeight: 'bold',
    transition: 'background-color 0.3s ease' // Płynna zmiana kolorów
  },
  carBodyFront: { width: '100px', textAlign: 'center', color: '#aaa', borderBottom: '2px solid #666' },
  carBodyRear: { width: '100px', textAlign: 'center', color: '#aaa', borderTop: '2px solid #666' },
  carBodyCenter: { 
    height: '150px', 
    width: '120px', 
    backgroundColor: '#222', 
    borderLeft: '2px solid #666', 
    borderRight: '2px solid #666',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  actionToast: {
    backgroundColor: '#007bff',
    padding: '5px 10px',
    borderRadius: '5px',
    fontSize: '12px'
  },
  debugBox: { marginTop: '30px', textAlign: 'left', fontSize: '12px', color: '#888' }
};

export default App;
